import React from "react";
import "./App.css";

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoaded : false,
      posts: [],
    }
  }

  componentDidMount() {
    //api  call

    fetch("https://movie-quote-api.herokuapp.com/v1/quote/")
      .then((res) => res.json())
      .then(json => {
        this.setState({ 
          isLoaded : true,
          posts: json 
        })
      });
  }

  render() {


    var { isLoaded, posts} = this.state;

    if(!isLoaded){

      return <div>loading.....</div>;
    }
    else{

    return (
      <div className="App">
        <ul>

        {posts.map(post =>(
          <li key = { post.id}>

            quote : {post.quote} | {post.show}
          </li>
        ) )
        };

        </ul>










