import React from "react";
import "./App.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items:[],
      DataisLoaded : false
    };
  }

  componentDidMount() {
    //api  call

    fetch("https://movie-quote-api.herokuapp.com/v1/quote")
      .then((resp) => resp.json())
      .then((json) =>{ this.setState({items:json, DataisLoaded : true});})
  }

  render() {

    const {DataisLoaded, items} = this.state;
    if(!DataisLoaded) return
    <div>
      <h1>plz wait</h1>
    </div>;

    return (
      <div className = "App">
        <h1 >
          items.map((item) =>(
            <ol key = { items.id }>

            </ol>
          )

          ))
        {/* {this.state.posts.map=(post)=>{(<p key={post.id}>{post.quote}.{post.role}.{post.show}.{post.contain_adult_lang}</p>)}} */}
        </h1>
      </div>
    );
  }
}

