import React from "react";
import "./App.css";

export default class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoaded : false,
      posts: [],
    }
  }

  componentDidMount() {
    //api  call

    fetch("https://movie-quote-api.herokuapp.com/v1/quote/")
      .then((res) => resp.json())
      .then((resp) => this.setState({ posts: resp }));
  }

  render() {
    return (
      <div className="Quote__Content">
        <h4>hi</h4>
      {
            (this.state.posts.map = (post) => {
              <p key={post.id}>
                {post.quote}.
                {post.role}.{post.show}.{post.contain_adult_lang}
              </p>;
            })}


      </div>
    );
  }
}
