import React from "react";
import "./App.css";

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      posts: [],
    };
  }

  componentDidMount(e) {

    e.prevent.default()
    //api  call

    fetch("https://movie-quote-api.herokuapp.com/v1/quote/")
      .then((res) => res.json())
      .then((json) => {
        this.setState({ 
          posts: json ,
        })
      });
  }

  render() {
    console.log(this.state.posts);
    return (
        <div>hello


          {this.state.posts.map((n,i)=>{
            return(
              <li key={i}>

                quote:{n.quote}

              </li>
            )
          })}
        </div>




    );
}

}
